import { initializeApp } from "firebase/app";
import {
  getAuth,
  signInWithRedirect,
  signInWithPopup,
  GoogleAuthProvider,
} from "firebase/auth";

import {
  getFirestore ,
  doc,
  getDoc,
  setDoc,
} from 'firebase/firestore';

const firebaseConfig = {
  apiKey: "AIzaSyAOC8wELR2mMi6QwT2hKYBSs5kHGm0G88U",
  authDomain: "crwn-ecom-ccd87.firebaseapp.com",
  projectId: "crwn-ecom-ccd87",
  storageBucket: "crwn-ecom-ccd87.appspot.com",
  messagingSenderId: "380267164331",
  appId: "1:380267164331:web:0f16b354cefd12c100fdee",
};

const firebaseApp = initializeApp(firebaseConfig);

const provider = new GoogleAuthProvider();
provider.setCustomParameters({
  prompt: 'select_account',
});

export const createUserProfileDocument = async (userAuth, additionalData) => {
  if (!userAuth) return;

  console.log(userAuth);
};

export const auth = getAuth();
export const signInWithGooglePopup = () => signInWithPopup(auth, provider);
